import React, { useState, useEffect } from 'react';
import './App.css';
import supabase from './supabaseClient';

function App() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState("");

  // Fetch todos from Supabase
  useEffect(() => {
    const fetchTodos = async () => {
      const { data, error } = await supabase
        .from('todos')
        .select('*');

      if (error) {
        console.error("Error fetching todos:", error);
      } else {
        setTodos(data);
      }
    };
    fetchTodos();
  }, []);

  // Add new todo
  const addTodo = async () => {
    if (newTodo.trim() === "") return;

    const { data, error } = await supabase
      .from('todos')
      .insert([{ task: newTodo, is_completed: false }]);

    if (error) {
      console.error("Error adding todo:", error);
    } else {
      setTodos([...todos, ...data]);
      setNewTodo("");
    }
  };

  // Mark todo as completed
  const toggleTodo = async (id, is_completed) => {
    const { data, error } = await supabase
      .from('todos')
      .update({ is_completed: !is_completed })
      .match({ id });

    if (error) {
      console.error("Error updating todo:", error);
    } else {
      const updatedTodos = todos.map(todo =>
        todo.id === id ? { ...todo, is_completed: !is_completed } : todo
      );
      setTodos(updatedTodos);
    }
  };

  // Delete todo
  const deleteTodo = async (id) => {
    const { error } = await supabase
      .from('todos')
      .delete()
      .match({ id });

    if (error) {
      console.error("Error deleting todo:", error);
    } else {
      setTodos(todos.filter(todo => todo.id !== id));
    }
  };

  return (
    <div className="App">
      <h1>Todo App</h1>
      <input
        type="text"
        value={newTodo}
        onChange={(e) => setNewTodo(e.target.value)}
        placeholder="Add new todo"
      />
      <button onClick={addTodo}>Add Todo</button>

      <ul>
        {todos.map(todo => (
          <li key={todo.id} style={{ textDecoration: todo.is_completed ? 'line-through' : 'none' }}>
            <span onClick={() => toggleTodo(todo.id, todo.is_completed)}>
              {todo.task}
            </span>
            <button onClick={() => deleteTodo(todo.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
