import { useState, useEffect } from 'react';
import { createClient } from '@supabase/supabase-js';
import './App.css';

// Supabase 配置
const supabaseUrl = 'https://niufliollslbmirwkdom.supabase.co';
const supabaseKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Im5pdWZsaW9sbHNsYm1pcndrZG9tIiwicm9sZSI6ImFub24iLCJpYXQiOjE3NDUwNTE1MzIsImV4cCI6MjA2MDYyNzUzMn0.gfVadPg08NnvhvUU_B8ZEhZ_x3peQvRncyrdQl-xlcA';
const supabase = createClient(supabaseUrl, supabaseKey);

// 日誌檢查
console.log('Supabase URL:', supabaseUrl);
console.log('Supabase Key:', supabaseKey);

// 控制排版：true 使用表格，false 使用卡片
const USE_TABLE_LAYOUT = false;

function App() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [error, setError] = useState(null);

  // 獲取所有待辦事項
  const fetchTodos = async () => {
    try {
      console.log('正在獲取待辦事項...');
      const { data, error } = await supabase
        .from('todos')
        .select('*');
      if (error) throw error;
      console.log('待辦事項數據:', data);
      setTodos(data || []);
    } catch (error) {
      console.error('獲取待辦事項失敗:', error);
      setError(`無法載入待辦事項：${error.message}`);
      setTodos([]);
    }
  };

  // 新增待辦事項
  const addTodo = async (e) => {
    e.preventDefault();
    if (!newTodo.trim()) return;
    try {
      console.log('正在新增待辦事項:', newTodo);
      const { data, error } = await supabase
        .from('todos')
        .insert([{ text: newTodo, completed: false }])
        .select();
      if (error) throw error;
      console.log('新增成功:', data);
      setTodos([data[0], ...todos]);
      setNewTodo('');
      setError(null);
    } catch (error) {
      console.error('新增待辦事項失敗:', error);
      setError(`無法新增待辦事項：${error.message}`);
    }
  };

  // 更新待辦事項完成狀態
  const toggleTodo = async (id, completed) => {
    try {
      console.log('正在切換待辦事項狀態:', { id, completed });
      const { error } = await supabase
        .from('todos')
        .update({ completed: !completed })
        .eq('id', id);
      if (error) throw error;
      setTodos(todos.map((todo) =>
        todo.id === id ? { ...todo, completed: !completed } : todo
      ));
      console.log('切換成功:', { id, newCompleted: !completed });
      setError(null);
    } catch (error) {
      console.error('更新待辦事項失敗:', error);
      setError(`無法更新待辦事項：${error.message}`);
    }
  };

  // 編輯待辦事項
  const updateTodo = async (id, newText) => {
    try {
      const { error } = await supabase
        .from('todos')
        .update({ text: newText })
        .eq('id', id);
      if (error) throw error;
      setTodos(todos.map((todo) =>
        todo.id === id ? { ...todo, text: newText } : todo
      ));
      setError(null);
    } catch (error) {
      console.error('編輯待辦事項失敗:', error);
      setError(`無法編輯待辦事項：${error.message}`);
    }
  };

  // 刪除待辦事項
  const deleteTodo = async (id) => {
    try {
      const { error } = await supabase
        .from('todos')
        .delete()
        .eq('id', id);
      if (error) throw error;
      setTodos(todos.filter((todo) => todo.id !== id));
      setError(null);
    } catch (error) {
      console.error('刪除待辦事項失敗:', error);
      setError(`無法刪除待辦事項：${error.message}`);
    }
  };

  // 初始化時獲取待辦事項
  useEffect(() => {
    fetchTodos();
  }, []);

  return (
    <div className="container mx-auto p-6 max-w-3xl bg-gray-50 min-h-screen">
      <h1 className="text-4xl font-extrabold mb-8 text-center text-gray-800">待辦事項</h1>

      {/* 錯誤訊息 */}
      {error && (
        <div className="mb-6 p-4 bg-red-100 text-red-700 rounded-xl shadow-md animate-fade-in">
          {error}
        </div>
      )}

      {/* 新增待辦事項表單 */}
      <form onSubmit={addTodo} className="mb-8 flex gap-3 bg-white p-4 rounded-xl shadow-md">
        <input
          type="text"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
          placeholder="輸入新的待辦事項"
          className="flex-1 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
        />
        <button
          type="submit"
          className="px-5 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition duration-200 shadow-sm"
        >
          新增
        </button>
      </form>

      {/* 待辦事項列表 */}
      {USE_TABLE_LAYOUT ? (
        <table className="w-full bg-white rounded-xl shadow-md overflow-hidden">
          <thead>
            <tr className="bg-gray-100 text-gray-700">
              <th className="p-4 text-left">完成</th>
              <th className="p-4 text-left">任務</th>
              <th className="p-4 text-right">操作</th>
            </tr>
          </thead>
          <tbody>
            {todos.length === 0 ? (
              <tr>
                <td colSpan="3" className="p-4 text-center text-gray-500">
                  暫無待辦事項
                </td>
              </tr>
            ) : (
              todos.map((todo) => (
                <TodoItem
                  key={todo.id}
                  todo={todo}
                  onToggle={toggleTodo}
                  onUpdate={updateTodo}
                  onDelete={deleteTodo}
                  isTableLayout={true}
                />
              ))
            )}
          </tbody>
        </table>
      ) : (
        <div className="grid gap-4">
          {todos.length === 0 ? (
            <p className="text-center text-gray-500 text-lg">暫無待辦事項</p>
          ) : (
            todos.map((todo) => (
              <TodoItem
                key={todo.id}
                todo={todo}
                onToggle={toggleTodo}
                onUpdate={updateTodo}
                onDelete={deleteTodo}
                isTableLayout={false}
              />
            ))
          )}
        </div>
      )}
    </div>
  );
}

// 單個待辦事項組件（支援卡片和表格排版）
function TodoItem({ todo, onToggle, onUpdate, onDelete, isTableLayout }) {
  const [isEditing, setIsEditing] = useState(false);
  const [editText, setEditText] = useState(todo.text);

  const handleUpdate = () => {
    if (editText.trim()) {
      onUpdate(todo.id, editText);
      setIsEditing(false);
    }
  };

  if (isTableLayout) {
    return (
      <tr className="border-b border-gray-200 hover:bg-gray-50">
        <td className="p-4">
          <input
            type="checkbox"
            checked={todo.completed}
            onChange={() => onToggle(todo.id, todo.completed)}
            className="h-5 w-5 text-blue-600 rounded focus:ring-blue-500"
          />
        </td>
        <td className="p-4">
          {isEditing ? (
            <input
              type="text"
              value={editText}
              onChange={(e) => setEditText(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          ) : (
            <span
              className={`text-lg ${todo.completed ? 'line-through text-gray-500' : 'text-gray-800'}`}
            >
              {todo.text}
            </span>
          )}
        </td>
        <td className="p-4 text-right">
          {isEditing ? (
            <>
              <button
                onClick={handleUpdate}
                className="px-3 py-1 bg-green-600 text-white rounded-lg hover:bg-green-700 transition duration-200 mr-2"
              >
                儲存
              </button>
              <button
                onClick={() => setIsEditing(false)}
                className="px-3 py-1 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition duration-200"
              >
                取消
              </button>
            </>
          ) : (
            <>
              <button
                onClick={() => setIsEditing(true)}
                className="px-3 py-1 bg-yellow-600 text-white rounded-lg hover:bg-yellow-700 transition duration-200 mr-2"
              >
                編輯
              </button>
              <button
                onClick={() => onDelete(todo.id)}
                className="px-3 py-1 bg-red-600 text-white rounded-lg hover:bg-red-700 transition duration-200"
              >
                刪除
              </button>
            </>
          )}
        </td>
      </tr>
    );
  }

  return (
    <div className="flex items-center p-4 bg-white rounded-xl shadow-md hover:shadow-lg transition duration-200 animate-fade-in">
      <input
        type="checkbox"
        checked={todo.completed}
        onChange={() => onToggle(todo.id, todo.completed)}
        className="mr-4 h-5 w-5 text-blue-600 rounded focus:ring-blue-500"
      />
      {isEditing ? (
        <div className="flex-1 flex gap-3">
          <input
            type="text"
            value={editText}
            onChange={(e) => setEditText(e.target.value)}
            className="flex-1 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
          />
          <button
            onClick={handleUpdate}
            className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition duration-200 shadow-sm"
          >
            儲存
          </button>
          <button
            onClick={() => setIsEditing(false)}
            className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition duration-200 shadow-sm"
          >
            取消
          </button>
        </div>
      ) : (
        <div className="flex-1 flex items-center justify-between">
          <span
            className={`text-lg font-medium ${todo.completed ? 'line-through text-gray-500' : 'text-gray-800'}`}
          >
            {todo.text}
          </span>
          <div className="flex gap-2">
            <button
              onClick={() => setIsEditing(true)}
              className="px-4 py-2 bg-yellow-600 text-white rounded-lg hover:bg-yellow-700 transition duration-200 shadow-sm"
            >
              編輯
            </button>
            <button
              onClick={() => onDelete(todo.id)}
              className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition duration-200 shadow-sm"
            >
              刪除
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
