import { useState, useEffect } from 'react';
import { createClient } from '@supabase/supabase-js';
import './App.css';

// Supabase 配置
const supabaseUrl = 'https://niufliollslbmirwkdom.supabase.co';
const supabaseKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Im5pdWZsaW9sbHNsYm1pcndrZG9tIiwicm9sZSI6ImFub24iLCJpYXQiOjE3NDUwNTE1MzIsImV4cCI6MjA2MDYyNzUzMn0.gfVadPg08NnvhvUU_B8ZEhZ_x3peQvRncyrdQl-xlcA';
const supabase = createClient(supabaseUrl, supabaseKey);

function App() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [error, setError] = useState(null);
  const [useTableLayout, setUseTableLayout] = useState(false);
  const [filter, setFilter] = useState('all'); // 'all', 'active', 'completed'

  // 獲取所有待辦事項
  const fetchTodos = async () => {
    try {
      console.log('正在獲取待辦事項...');
      const { data, error } = await supabase
        .from('todos')
        .select('*')
        .order('created_at', { ascending: false });
      if (error) throw error;
      console.log('待辦事項數據:', data);
      setTodos(data || []);
    } catch (error) {
      console.error('獲取待辦事項失敗:', error);
      setError(`無法載入待辦事項：${error.message}`);
      setTodos([]);
    }
  };

  // 新增待辦事項
  const addTodo = async (e) => {
    e.preventDefault();
    if (!newTodo.trim()) return;
    try {
      console.log('正在新增待辦事項:', newTodo);
      const { data, error } = await supabase
        .from('todos')
        .insert([{ text: newTodo, completed: false, created_at: new Date().toISOString() }])
        .select();
      if (error) throw error;
      console.log('新增成功:', data);
      setTodos([data[0], ...todos]);
      setNewTodo('');
      setError(null);
    } catch (error) {
      console.error('新增待辦事項失敗:', error);
      setError(`無法新增待辦事項：${error.message}`);
    }
  };

  // 更新待辦事項完成狀態
  const toggleTodo = async (id, completed) => {
    try {
      console.log('正在切換待辦事項狀態:', { id, completed });
      const { error } = await supabase
        .from('todos')
        .update({ completed: !completed })
        .eq('id', id);
      if (error) throw error;
      setTodos(todos.map((todo) =>
        todo.id === id ? { ...todo, completed: !completed } : todo
      ));
      console.log('切換成功:', { id, newCompleted: !completed });
      setError(null);
    } catch (error) {
      console.error('更新待辦事項失敗:', error);
      setError(`無法更新待辦事項：${error.message}`);
    }
  };

  // 編輯待辦事項
  const updateTodo = async (id, newText) => {
    try {
      const { error } = await supabase
        .from('todos')
        .update({ text: newText })
        .eq('id', id);
      if (error) throw error;
      setTodos(todos.map((todo) =>
        todo.id === id ? { ...todo, text: newText } : todo
      ));
      setError(null);
    } catch (error) {
      console.error('編輯待辦事項失敗:', error);
      setError(`無法編輯待辦事項：${error.message}`);
    }
  };

  // 刪除待辦事項
  const deleteTodo = async (id) => {
    try {
      const { error } = await supabase
        .from('todos')
        .delete()
        .eq('id', id);
      if (error) throw error;
      setTodos(todos.filter((todo) => todo.id !== id));
      setError(null);
    } catch (error) {
      console.error('刪除待辦事項失敗:', error);
      setError(`無法刪除待辦事項：${error.message}`);
    }
  };

  // 篩選待辦事項
  const filteredTodos = todos.filter(todo => {
    if (filter === 'active') return !todo.completed;
    if (filter === 'completed') return todo.completed;
    return true; // 'all'
  });

  // 初始化時獲取待辦事項
  useEffect(() => {
    fetchTodos();
  }, []);

  return (
    <div className="container mx-auto p-6 max-w-3xl bg-gray-50 min-h-screen">
      <h1 className="text-4xl font-extrabold mb-8 text-center text-gray-800">待辦事項</h1>

      {/* 錯誤訊息 */}
      {error && (
        <div className="mb-6 p-4 bg-red-100 text-red-700 rounded-xl shadow-md animate-fade-in">
          {error}
          <button 
            onClick={() => setError(null)} 
            className="ml-2 text-red-700 font-bold"
          >
            ✕
          </button>
        </div>
      )}

      {/* 新增待辦事項表單 */}
      <form onSubmit={addTodo} className="mb-6 flex gap-3 bg-white p-4 rounded-xl shadow-md">
        <input
          type="text"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
          placeholder="輸入新的待辦事項"
          className="flex-1 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
        />
        <button
          type="submit"
          className="px-5 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition duration-200 shadow-sm"
        >
          新增
        </button>
      </form>

      {/* 控制面板 */}
      <div className="mb-6 flex flex-wrap gap-3 justify-between items-center bg-white p-4 rounded-xl shadow-md">
        <div className="flex gap-2">
          <button 
            onClick={() => setFilter('all')}
            className={`px-3 py-2 rounded-lg transition ${filter === 'all' ? 'bg-blue-100 text-blue-700' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}
          >
            全部
          </button>
          <button 
            onClick={() => setFilter('active')}
            className={`px-3 py-2 rounded-lg transition ${filter === 'active' ? 'bg-blue-100 text-blue-700' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}
          >
            未完成
          </button>
          <button 
            onClick={() => setFilter('completed')}
            className={`px-3 py-2 rounded-lg transition ${filter === 'completed' ? 'bg-blue-100 text-blue-700' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}
          >
            已完成
          </button>
        </div>
        <div className="flex items-center gap-2">
          <span className="text-gray-700">佈局:</span>
          <button 
            onClick={() => setUseTableLayout(false)}
            className={`px-3 py-2 rounded-lg transition ${!useTableLayout ? 'bg-blue-100 text-blue-700' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}
          >
            卡片
          </button>
          <button 
            onClick={() => setUseTableLayout(true)}
            className={`px-3 py-2 rounded-lg transition ${useTableLayout ? 'bg-blue-100 text-blue-700' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}
          >
            表格
          </button>
        </div>
      </div>

      {/* 待辦事項列表 */}
      {useTableLayout ? (
        <div className="overflow-hidden bg-white rounded-xl shadow-md">
          <table className="w-full">
            <thead>
              <tr className="bg-gray-100 text-gray-700">
                <th className="p-4 text-left">完成</th>
                <th className="p-4 text-left">任務</th>
                <th className="p-4 text-right">操作</th>
              </tr>
            </thead>
            <tbody>
              {filteredTodos.length === 0 ? (
                <tr>
                  <td colSpan="3" className="p-6 text-center text-gray-500">
                    {filter === 'all' ? '暫無待辦事項' : 
                     filter === 'active' ? '暫無未完成事項' : 
                     '暫無已完成事項'}
                  </td>
                </tr>
              ) : (
                filteredTodos.map((todo) => (
                  <TodoItem
                    key={todo.id}
                    todo={todo}
                    onToggle={toggleTodo}
                    onUpdate={updateTodo}
                    onDelete={deleteTodo}
                    isTableLayout={true}
                  />
                ))
              )}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="grid gap-4">
          {filteredTodos.length === 0 ? (
            <div className="p-6 text-center text-gray-500 bg-white rounded-xl shadow-md">
              {filter === 'all' ? '暫無待辦事項' : 
               filter === 'active' ? '暫無未完成事項' : 
               '暫無已完成事項'}
            </div>
          ) : (
            filteredTodos.map((todo) => (
              <TodoItem
                key={todo.id}
                todo={todo}
                onToggle={toggleTodo}
                onUpdate={updateTodo}
                onDelete={deleteTodo}
                isTableLayout={false}
              />
            ))
          )}
        </div>
      )}
    </div>
  );
}

// 單個待辦事項組件（支援卡片和表格排版）
function TodoItem({ todo, onToggle, onUpdate, onDelete, isTableLayout }) {
  const [isEditing, setIsEditing] = useState(false);
  const [editText, setEditText] = useState(todo.text);

  const handleUpdate = () => {
    if (editText.trim()) {
      onUpdate(todo.id, editText);
      setIsEditing(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleUpdate();
    } else if (e.key === 'Escape') {
      setIsEditing(false);
      setEditText(todo.text);
    }
  };

  if (isTableLayout) {
    return (
      <tr className="border-b border-gray-200 hover:bg-gray-50 transition duration-150">
        <td className="p-4">
          <input
            type="checkbox"
            checked={todo.completed}
            onChange={() => onToggle(todo.id, todo.completed)}
            className="h-5 w-5 text-blue-600 rounded focus:ring-blue-500 cursor-pointer"
          />
        </td>
        <td className="p-4">
          {isEditing ? (
            <input
              type="text"
              value={editText}
              onChange={(e) => setEditText(e.target.value)}
              onKeyDown={handleKeyDown}
              autoFocus
              className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          ) : (
            <span
              className={`text-lg ${todo.completed ? 'line-through text-gray-500' : 'text-gray-800'}`}
            >
              {todo.text}
            </span>
          )}
        </td>
        <td className="p-4 text-right">
          {isEditing ? (
            <>
              <button
                onClick={handleUpdate}
                className="px-3 py-1 bg-green-600 text-white rounded-lg hover:bg-green-700 transition duration-200 mr-2"
              >
                儲存
              </button>
              <button
                onClick={() => {
                  setIsEditing(false);
                  setEditText(todo.text);
                }}
                className="px-3 py-1 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition duration-200"
              >
                取消
              </button>
            </>
          ) : (
            <>
              <button
                onClick={() => setIsEditing(true)}
                className="px-3 py-1 bg-yellow-600 text-white rounded-lg hover:bg-yellow-700 transition duration-200 mr-2"
              >
                編輯
              </button>
              <button
                onClick={() => onDelete(todo.id)}
                className="px-3 py-1 bg-red-600 text-white rounded-lg hover:bg-red-700 transition duration-200"
              >
                刪除
              </button>
            </>
          )}
        </td>
      </tr>
    );
  }

  return (
    <div className={`flex items-center p-4 bg-white rounded-xl shadow-md hover:shadow-lg transition duration-200 ${todo.completed ? 'bg-gray-50' : ''}`}>
      <input
        type="checkbox"
        checked={todo.completed}
        onChange={() => onToggle(todo.id, todo.completed)}
        className="mr-4 h-5 w-5 text-blue-600 rounded focus:ring-blue-500 cursor-pointer"
      />
      {isEditing ? (
        <div className="flex-1 flex gap-3">
          <input
            type="text"
            value={editText}
            onChange={(e) => setEditText(e.target.value)}
            onKeyDown={handleKeyDown}
            autoFocus
            className="flex-1 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
          />
          <button
            onClick={handleUpdate}
            className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition duration-200 shadow-sm"
          >
            儲存
          </button>
          <button
            onClick={() => {
              setIsEditing(false);
              setEditText(todo.text);
            }}
            className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition duration-200 shadow-sm"
          >
            取消
          </button>
        </div>
      ) : (
        <div className="flex-1 flex items-center justify-between">
          <span
            className={`text-lg font-medium ${todo.completed ? 'line-through text-gray-500' : 'text-gray-800'}`}
          >
            {todo.text}
          </span>
          <div className="flex gap-2">
            <button
              onClick={() => setIsEditing(true)}
              className="px-4 py-2 bg-yellow-600 text-white rounded-lg hover:bg-yellow-700 transition duration-200 shadow-sm"
            >
              編輯
            </button>
            <button
              onClick={() => onDelete(todo.id)}
              className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition duration-200 shadow-sm"
            >
              刪除
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
