import { useState, useEffect } from 'react';
import { createClient } from '@supabase/supabase-js';
import './App.css';

// Supabase 配置
const supabaseUrl = process.env.REACT_APP_SUPABASE_URL || 'https://your-project-ref.supabase.co'; // 替換為你的實際 URL
const supabaseKey = process.env.REACT_APP_SUPABASE_KEY || 'your-anon-key'; // 替換為你的實際金鑰
const supabase = createClient(supabaseUrl, supabaseKey);

function App() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [error, setError] = useState(null);

  // 獲取所有待辦事項
  const fetchTodos = async () => {
    try {
      console.log('正在獲取待辦事項...');
      const { data, error } = await supabase
        .from('todos')
        .select('*')
        .order('created_at', { ascending: false });
      if (error) throw error;
      console.log('待辦事項數據:', data);
      setTodos(data || []);
    } catch (error) {
      console.error('獲取待辦事項失敗:', error.message);
      setError('無法載入待辦事項，請檢查網路或 Supabase 設置');
      setTodos([]);
    }
  };

  // 新增待辦事項
  const addTodo = async (e) => {
    e.preventDefault();
    if (!newTodo.trim()) return;
    try {
      const { data, error } = await supabase
        .from('todos')
        .insert([{ text: newTodo, completed: false }])
        .select();
      if (error) throw error;
      setTodos([data[0], ...todos]);
      setNewTodo('');
      setError(null);
    } catch (error) {
      console.error('新增待辦事項失敗:', error.message);
      setError('無法新增待辦事項，請稍後再試');
    }
  };

  // 更新待辦事項完成狀態
  const toggleTodo = async (id, completed) => {
    try {
      const { error } = await supabase
        .from('todos')
        .update({ completed: !completed })
        .eq('id', id);
      if (error) throw error;
      setTodos(todos.map((todo) =>
        todo.id === id ? { ...todo, completed: !completed } : todo
      ));
      setError(null);
    } catch (error) {
      console.error('更新待辦事項失敗:', error.message);
      setError('無法更新待辦事項，請稍後再試');
    }
  };

  // 編輯待辦事項
  const updateTodo = async (id, newText) => {
    try {
      const { error } = await supabase
        .from('todos')
        .update({ text: newText })
        .eq('id', id);
      if (error) throw error;
      setTodos(todos.map((todo) =>
        todo.id === id ? { ...todo, text: newText } : todo
      ));
      setError(null);
    } catch (error) {
      console.error('編輯待辦事項失敗:', error.message);
      setError('無法編輯待辦事項，請稍後再試');
    }
  };

  // 刪除待辦事項
  const deleteTodo = async (id) => {
    try {
      const { error } = await supabase
        .from('todos')
        .delete()
        .eq('id', id);
      if (error) throw error;
      setTodos(todos.filter((todo) => todo.id !== id));
      setError(null);
    } catch (error) {
      console.error('刪除待辦事項失敗:', error.message);
      setError('無法刪除待辦事項，請稍後再試');
    }
  };

  // 初始化時獲取待辦事項
  useEffect(() => {
    fetchTodos();
  }, []);

  return (
    <div className="container mx-auto p-4 max-w-2xl">
      <h1 className="text-3xl font-bold mb-6 text-center">待辦事項</h1>

      {/* 錯誤訊息 */}
      {error && (
        <div className="mb-4 p-3 bg-red-100 text-red-700 rounded-lg">
          {error}
        </div>
      )}

      {/* 新增待辦事項表單 */}
      <form onSubmit={addTodo} className="mb-6 flex gap-2">
        <input
          type="text"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
          placeholder="輸入新的待辦事項"
          className="flex-1 p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          type="submit"
          className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
        >
          新增
        </button>
      </form>

      {/* 待辦事項列表 */}
      <div className="grid gap-4">
        {todos.length === 0 ? (
          <p className="text-center text-gray-500">暫無待辦事項</p>
        ) : (
          todos.map((todo) => (
            <TodoItem
              key={todo.id}
              todo={todo}
              onToggle={toggleTodo}
              onUpdate={updateTodo}
              onDelete={deleteTodo}
            />
          ))
        )}
      </div>
    </div>
  );
}

// 單個待辦事項組件
function TodoItem({ todo, onToggle, onUpdate, onDelete }) {
  const [isEditing, setIsEditing] = useState(false);
  const [editText, setEditText] = useState(todo.text);

  const handleUpdate = () => {
    if (editText.trim()) {
      onUpdate(todo.id, editText);
      setIsEditing(false);
    }
  };

  return (
    <div className="flex items-center p-4 bg-white rounded-lg shadow-md hover:shadow-lg transition">
      <input
        type="checkbox"
        checked={todo.completed}
        onChange={() => onToggle(todo.id, todo.completed)}
        className="mr-3 h-5 w-5"
      />
      {isEditing ? (
        <div className="flex-1 flex gap-2">
          <input
            type="text"
            value={editText}
            onChange={(e) => setEditText(e.target.value)}
            className="flex-1 p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            onClick={handleUpdate}
            className="px-3 py-1 bg-green-500 text-white rounded-lg hover:bg-green-600"
          >
            儲存
          </button>
          <button
            onClick={() => setIsEditing(false)}
            className="px-3 py-1 bg-gray-500 text-white rounded-lg hover:bg-gray-600"
          >
            取消
          </button>
        </div>
      ) : (
        <div className="flex-1 flex items-center justify-between">
          <span
            className={`text-lg ${todo.completed ? 'line-through text-gray-500' : ''}`}
          >
            {todo.text}
          </span>
          <div className="flex gap-2">
            <button
              onClick={() => setIsEditing(true)}
              className="px-3 py-1 bg-yellow-500 text-white rounded-lg hover:bg-yellow-600"
            >
              編輯
            </button>
            <button
              onClick={() => onDelete(todo.id)}
              className="px-3 py-1 bg-red-500 text-white rounded-lg hover:bg-red-600"
            >
              刪除
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
