import { useState, useEffect } from 'react';
import supabase from './supabaseClient';

function App() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState('');

  // ËÆÄÂèñÂæÖËæ¶‰∫ãÈ†Ö
  const fetchTodos = async () => {
    const { data, error } = await supabase
      .from('todos')
      .select('*')
      .order('id', { ascending: false }); // ÊåâÁÖß id ÈôçÂ∫èÊéíÂàó

    if (error) {
      console.error('Error fetching todos:', error);
    } else {
      setTodos(data); // Êõ¥Êñ∞ÂæÖËæ¶‰∫ãÈ†ÖÂàóË°®
    }
  };

  // Êñ∞Â¢ûÂæÖËæ¶‰∫ãÈ†Ö
  const addTodo = async () => {
    if (!input.trim()) return; // Á¢∫‰øùËº∏ÂÖ•‰∏çÁÇ∫Á©∫

    const { data, error } = await supabase
      .from('todos')
      .insert([{ text: input, is_complete: false }])
      .single(); // ÊèíÂÖ•ÂñÆ‰∏ÄË≥áÊñô

    if (error) {
      console.error('Error adding todo:', error);
    } else {
      setTodos([data, ...todos]); // Êõ¥Êñ∞ÂæÖËæ¶Ê∏ÖÂñÆ
      setInput(''); // Ê∏ÖÁ©∫Ëº∏ÂÖ•Ê°Ü
    }
  };

  // Êõ¥Êñ∞ÂæÖËæ¶‰∫ãÈ†ÖÁöÑÂÆåÊàêÁãÄÊÖã
  const toggleTodo = async (id, isComplete) => {
    const { data, error } = await supabase
      .from('todos')
      .update({ is_complete: !isComplete })
      .eq('id', id) // ÊåáÂÆöÂæÖËæ¶‰∫ãÈ†ÖÁöÑ id
      .single(); // Âè™Êõ¥Êñ∞‰∏ÄÁ≠ÜË≥áÊñô

    if (error) {
      console.error('Error updating todo:', error);
    } else {
      setTodos(todos.map(todo => (todo.id === id ? data : todo))); // Êõ¥Êñ∞ÁãÄÊÖã
    }
  };

  // Âà™Èô§ÂæÖËæ¶‰∫ãÈ†Ö
  const deleteTodo = async (id) => {
    const { error } = await supabase
      .from('todos')
      .delete()
      .eq('id', id); // Ê†πÊìö id Âà™Èô§

    if (error) {
      console.error('Error deleting todo:', error);
    } else {
      setTodos(todos.filter(todo => todo.id !== id)); // Êõ¥Êñ∞ÂæÖËæ¶Ê∏ÖÂñÆ
    }
  };

  // ‰ΩøÁî® useEffect ÂàùÂßãÂåñÊôÇËÆÄÂèñÂæÖËæ¶‰∫ãÈ†Ö
  useEffect(() => {
    fetchTodos();
  }, []);

  return (
    <div style={{ maxWidth: 500, margin: '50px auto', fontFamily: 'sans-serif' }}>
      <h1>üìù ÊàëÁöÑÂæÖËæ¶Ê∏ÖÂñÆ</h1>

      <div style={{ display: 'flex', marginBottom: '1rem' }}>
        <input
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          placeholder="Ëº∏ÂÖ•‰ªªÂãô..."
          style={{ flex: 1, padding: '8px' }}
        />
        <button onClick={addTodo} style={{ padding: '8px 12px', marginLeft: '8px' }}>
          Êñ∞Â¢û
        </button>
      </div>

      <ul style={{ listStyle: 'none', padding: 0 }}>
        {todos.map(todo => (
          <li
            key={todo.id}
            onClick={() => toggleTodo(todo.id, todo.is_complete)}
            style={{
              padding: '8px',
              marginBottom: '4px',
              cursor: 'pointer',
              textDecoration: todo.is_complete ? 'line-through' : 'none',
              backgroundColor: '#f0f0f0',
              borderRadius: '4px',
            }}
          >
            {todo.text}
            <button
              onClick={(e) => {
                e.stopPropagation();
                deleteTodo(todo.id);
              }}
              style={{
                marginLeft: '10px',
                padding: '5px 8px',
                backgroundColor: '#ff4747',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
              }}
            >
              Âà™Èô§
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
